<?php    function calendar($month, $year, $dane){        $date = mktime(12, 0, 0, $month, 0, $year);        $date_2 = mktime(12, 0, 0, $month, 1, $year);        $daysInMonth = date("t", $date_2);        $offset = date("w", $date);        $rows = 1;        $month_show = array();        $month_show['01'] = 'Styczeń';        $month_show['02'] = 'Luty';        $month_show['03'] = 'Marzec';        $month_show['04'] = 'Kwiecień';        $month_show['05'] = 'Maj';        $month_show['06'] = 'Czerwiec';        $month_show['07'] = 'Lipiec';        $month_show['08'] = 'Sierpień';        $month_show['09'] = 'Wrzesień';        $month_show['10'] = 'Październik';        $month_show['11'] = 'Listopad';        $month_show['12'] = 'Grudzień';        $next_month = ($month + 1) > 12 ? 1 : ($month + 1);        $next_month = $next_month < 10 ? '0'.$next_month : $next_month;        $next_year = ($month + 1) > 12 ? $year + 1 : $year;        $next_link = '?data='.$next_month.'-'.$next_year.'#kalendarz';        $prev_month = ($month - 1) == 0 ? 12 : ($month - 1);        $prev_month = $prev_month < 10 ? '0'.$prev_month : $prev_month;        $prev_year = ($month - 1) == 0 ? $year - 1 : $year;        $prev_link = '?data='.$prev_month.'-'.$prev_year.'#kalendarz';        echo '<div class="tytul">';        //echo '<a href="'.$prev_link.'"><</a>';        //echo '<a href="'.$next_link.'">></a>';        echo $month_show[$month].' '.$year;        echo '</div>';        echo '<table border="0" cellpadding="0" cellspacing="1" class="tabela">';        echo "<tr><th>PN</th><th>WT</th><th>ŚR</th><th>CZ</th><th>PT</th><th>SB</th><th>ND</th></tr>";        echo "<tr>";        for($i = 1; $i <= $offset; $i++){            echo "<td></td>";        }        for($day = 1; $day <= $daysInMonth; $day++){            if(($day + $offset - 1) % 7 == 0 && $day != 1){                echo "</tr><tr>";                $rows++;            }            echo '<td>';            echo $day;            if(isset($dane[$day]) && $dane[$day] > 0) echo ' <div class="numer">'.$dane[$day].'</div>';            echo '</td>';        }        while( ($day + $offset) <= $rows * 7){            echo "<td></td>";            $day++;        }        echo "</tr>";        echo "</table>";     }    $this -> load -> view('header', array('uzytkownik' => $uzytkownik, 'klient' => isset($klient) ? $klient : false));		$etapy = $this -> load -> model('klienci') -> etapy();    	// kalendarz    	echo '<div class="kalendarz">';    echo '<table border="0" cellpadding="0" cellspacing="20" style="border-collapse: separate; border-spacing: 10px;"><tr>';    $current_month = date("m");    $current_year = date("Y");    $previous_date = date('Y-m', strtotime('-1 month', strtotime("$current_year-$current_month-01")));    $previous_date = explode('-', $previous_date);    $previous_year = $previous_date[0];    $previous_month = $previous_date[1];    $next_date = date('Y-m', strtotime('+1 month', strtotime("$current_year-$current_month-01")));    $next_date = explode('-', $next_date);    $next_year = $next_date[0];    $next_month = $next_date[1];    echo '<td>';        calendar($previous_month, $previous_year, $this -> load -> model('klienci') -> ilosc_zaplanowanych_kontaktow_w_miesiacu($previous_month, $previous_year, $uzytkownik));    echo '</td>';    echo '<td class="separator"></td>';    echo '<td>';        calendar($current_month, $current_year, $this -> load -> model('klienci') -> ilosc_zaplanowanych_kontaktow_w_miesiacu($current_month, $current_year, $uzytkownik));    echo '</td>';    echo '<td class="separator"></td>';    echo '<td>';        calendar($next_month, $next_year, $this -> load -> model('klienci') -> ilosc_zaplanowanych_kontaktow_w_miesiacu($next_month, $next_year, $uzytkownik));    echo '</td>';    echo '</tr></table>';    echo '</div>';    ?>    <script type="text/javascript">        $(document).ready(function(){           